app.controller('taskController', taskFunction);

function taskFunction($scope, taskResource, UserClientResource, $location, $timeout, statusResource, moduleResource, urgencyResource, loginService) {

  var userId = loginService.getUserId();
  $scope.userId = userId;

  $scope.vm = {
    done: '',
    client: ''
  };

  $scope.searchReferent = '';
  
  $scope.exportAction = function(type) {
    switch (type) {
      case 'pdf':
        $scope.$broadcast('export-pdf', {});
        break;
      case 'excel':
        $scope.$broadcast('export-excel', {});
        break;
      case 'doc':
        $scope.$broadcast('export-doc', {});
        break;
      default:
        console.log('no event caught');
    }
  }

  $scope.filterUser = function(value) {
    if (value) {
      updateMyProfile();
    }
  }

  $scope.getHeader = function() {
    return ["id", "sar", "title", "description", "creation_date", "done", "start_date", "finish_date", "client", "user", "priority", "urgency", "estimation_hours", "module"]
  };

  $scope.filterFullname = function(data) {
    return (data.user.first_name.toLowerCase() + data.user.last_name.toLowerCase()).indexOf($scope.searchReferent.toLowerCase()) >= 0;
  };

  function getTasks() {
    var tasks = [];
    var indArray = [];
    var user;
    var rel = {};

    loginService.getUser().then(function(user_data){
      rel = { user: user_data };
    });
    
    UserClientResource.query({
      userR: userId
    }, function(data) {
      data.push(rel);
      data.forEach(function(rel) {
        taskResource.query({
          done: 'False',
          user_client: userId
        }, function(data) {
          if (data.length > 0) {
            data.forEach(function(element){
              // dont repeat tasks
              if(indArray.indexOf(element.id) === -1){
                indArray.push(element.id)  
                tasks.push(element);  
              }
            })
          }
        });
      })
      $scope.tasks = tasks;
    })
  }
  getTasks();

  $scope.doneTask = function(taskId) {
    taskResource.get({
      taskId: taskId
    }, function(task) {
      task.done = true;
      taskResource.update({
        taskId: task.id
      }, task);
    });
    getTasks();
  };

  $scope.deleteTask = function(taskId) {
    taskResource.delete({
      id: taskId
    });
    $timeout(getTasks, 500);
  };

  function getClients() {
    var all = {
      user: {
        id: '',
        first_name: 'Todos',
        last_name: '',
      },
      userR: {
        id: '',
        first_name: 'Todos',
        last_name: '',
      }
    };

    UserClientResource.query({
      user : userId
    }, function(data) {
      UserClientResource.query({
        userR : userId
      }, function(data2) {
        Array.prototype.push.apply(data, data2);
        data.unshift(all);
        $scope.clients = data;
      });
    });
  };
  getClients();

  UserClientResource.query({
    user: userId,
    relation: 'D'
  }, function(data) {
    $scope.clientsM = data;
  });

  statusResource.query({}, function(data) {
    $scope.Tstatus = data;
  });

  moduleResource.query({}, function(data) {
    $scope.modules = data;
  });

  urgencyResource.query({}, function(data) {
    $scope.urgency = data;
  });

  $scope.newTask = function() {
    $scope.task.user = loginService.getUserId();;
    taskResource.save($scope.task);
    $scope.task = null;
    $('#myModal').modal('hide');
    $timeout(getTasks, 500);
  };
}

